var express = require("express");
var app = express();
var bodyParser = require("body-parser");
var mysql = require("mysql");

app.use(bodyParser.json());
app.use(
  bodyParser.urlencoded({
    extended: true,
  })
);

app.use("/js", express.static(__dirname + "/js"));
app.use("/public", express.static(__dirname + "/public"));

app.get(["/", "/index.html"], (req, res) => {
  res.sendFile(__dirname + "/public/index.html");
});

// connection configurations
var dbConn = mysql.createConnection({
  host: "13.49.31.59",
  user: "kuser",
  password: "sejhkweb",
  database: "members",
});

dbConn.connect();

// get all members
app.get("/member", function (req, res) {
  dbConn.query("SELECT * FROM member_table", function (error, results, fields) {
    if (error) throw error;
    return res.send({ error: false, data: results, message: "members list." });
  });
});

// Json Web Token
const jwt = require("jsonwebtoken");
const { auth } = require("./auth.js");
const config = require("./config.js");
// login process
app.post("/login_process", async function (req, res, next) {
  let email = req.body.email;
  let pw = req.body.pw;

  if (email && pw) {
    dbConn.query(
      "SELECT * FROM member_table WHERE email = ? AND pw = ?",
      [email, pw],
      async function (error, results, fields) {
        if (error) throw error;
        if (results.length > 0) {
          //		   req.session.is_logined = true;
          //		   req.session.nickname = username;
          //		   req.session.save(function () {
          // res.send({ results, message: "you are loginned" });

          try {
            const accessToken = await new Promise((resolve, reject) => {
              jwt.sign(
                {
                  email: email,
                  pw: pw,
                },
                config.secretKey,
                { expiresIn: "1h" },
                (err, token) => {
                  if (err) {
                    reject(err);
                  } else {
                    resolve(token);
                  }
                }
              );
            });
            res.json({ success: true, accessToken: accessToken });
          } catch (err) {
            console.log(err);
            res
              .status(401)
              .json({ success: false, errormessage: "token sign fail" });
          }
          //		   });
        } else {
          // res.send({
          //   error: false,
          //   data: results,
          //   message: "Login information is not correct",
          // });
          res.status(401).json({
            success: false,
            errormessage: "email and password are not identical",
          });
        }
      }
    );
  } else {
    // res.send({ error: false, message: "Please insert username and password" });
    res.status(401).json({
      success: false,
      errormessage: "email and password are not identical",
    });
  }
});

